function runslider(selector, n) {

    var create = (function (n) {
        var imagesDirectory = './img/';
        var data =  [];
        for (var i = 0; i <= n; i += 1) {
            data[i] = {};
            data[i].title = 'img ' + (i + 1);
            data[i].src = imagesDirectory + 'img_' + (i + 1) + '.jpg';
        }
        return { items: data };
    } (n));


    var bigContainer = document.createElement('div');
    bigContainer.id = 'container';

    var bigTitle = document.createElement('p');
    bigTitle.innerHTML = create.items[0].title;

    var bigImage = document.createElement('img');
    bigImage.src = create.items[0].src;

    bigContainer.appendChild(bigTitle);
    bigContainer.appendChild(bigImage);

    var topContainer = document.createElement('div');
    topContainer.id = 'img-container';

    var data = create.items;
    for (var i = 0; i < data.length - 1; i += 1) {
        var imageContainer = document.createElement('div');
        imageContainer.classList.add('image-container');

        var smallTitle = document.createElement('p');
        smallTitle.innerHTML = data[i].title;

        var smallImage = document.createElement('img');
        smallImage.src = data[i].src;

        imageContainer.appendChild(smallTitle);
        imageContainer.appendChild(smallImage);

        topContainer.appendChild(imageContainer);
    }

    

    document.getElementById('container').appendChild(topContainer);
    document.getElementById('container').appendChild(bigContainer);

    bigContainer.addEventListener('mouseover', function (ev) {
        var target = ev.target;
        if (!target.classList.contains('image-container')) {
            return;
        }
        target.style.backgroundColor = '';
    });

    bigContainer.addEventListener('mouseout', function (ev) {
        var target = ev.target;
        if (!target.classList.contains('image-container')) {
            return;
        }
        target.style.backgroundColor = '';
    });

    bigContainer.addEventListener('click', function (ev) {

        var target = ev.target;
        if (!target.classList.contains('image-container')) {
            target = target.parentNode;
        }

        var smallTitle = target.querySelector('p');
        var smallImage = target.querySelector('img');

        bigTitle.innerHTML = smallTitle.innerHTML;
        bigImage.src = smallImage.src;
    });

   
}